import React, { useEffect, useState } from "react";
import axios from "axios";

function JobSuggestions({ keywords = [], summary = "" }) {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activePracticeIndex, setActivePracticeIndex] = useState(null);
  const [selectedLevel, setSelectedLevel] = useState({});
  const [questions, setQuestions] = useState({});
  const [answers, setAnswers] = useState({});
  const [showAnswers, setShowAnswers] = useState({});
  const [questionLoading, setQuestionLoading] = useState(false);

  useEffect(() => {
    const fetchJobs = async () => {
      if (keywords.length === 0 && !summary) {
        setLoading(false);
        return;
      }

      const query = keywords.slice(0, 5).join(" ") || summary.slice(0, 100);

      const options = {
        method: 'GET',
        url: 'https://jsearch.p.rapidapi.com/search',
        params: {
          query: query || 'software engineer',
          page: '1',
          num_pages: '1'
        },
        headers: {
          'X-RapidAPI-Key': '5f0a45efc5msha459b3624abbf5fp178efejsn4ade39898509',
          'X-RapidAPI-Host': 'jsearch.p.rapidapi.com'
        }
      };

      try {
        const res = await axios.request(options);
        setJobs(res.data.data || []);
      } catch (err) {
        console.error("Failed to fetch jobs:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchJobs();
  }, [keywords, summary]);

  const handlePracticeClick = async (jobTitle, index) => {
    const level = selectedLevel[index] || "Beginner";

    // If already active, toggle off
    if (activePracticeIndex === index) {
      setActivePracticeIndex(null);
      return;
    }

    setActivePracticeIndex(index);
    setQuestionLoading(true);

    try {
      const res = await axios.post("http://localhost:5000/api/interview-questions", {
        job_role: jobTitle,
        level: level
      });

      setQuestions({ ...questions, [index]: res.data.questions });
      setAnswers({ ...answers, [index]: res.data.answers });
      setShowAnswers({}); // reset visibility
    } catch (err) {
      console.error("Error fetching questions", err);
      setQuestions({ ...questions, [index]: ["Failed to load questions."] });
    } finally {
      setQuestionLoading(false);
    }
  };

  const toggleAnswer = (jobIndex, questionIndex) => {
    const key = `${jobIndex}-${questionIndex}`;
    setShowAnswers((prev) => ({ ...prev, [key]: !prev[key] }));
  };

  return (
    <div style={{ marginTop: "20px", backgroundColor: "#fff", padding: "20px", borderRadius: "10px", boxShadow: "0 0 10px #eee" }}>
      <h2>ðŸ”¥ Job Suggestions</h2>
      {loading ? (
        <p>Loading job listings...</p>
      ) : jobs.length > 0 ? (
        <ul style={{ listStyle: "none", padding: 0 }}>
          {jobs.map((job, index) => (
            <li key={index} style={{ marginBottom: "20px", borderBottom: "1px solid #ddd", paddingBottom: "15px" }}>
              <strong>{job.job_title}</strong><br />
              {job.employer_name} â€“ {job.job_city}, {job.job_country}<br />
              <a href={job.job_apply_link} target="_blank" rel="noopener noreferrer">Apply Now</a>
              <br /><br />
              <label>Practice Level: </label>
              <select
                value={selectedLevel[index] || "Beginner"}
                onChange={(e) => setSelectedLevel({ ...selectedLevel, [index]: e.target.value })}
              >
                <option value="Beginner">Beginner</option>
                <option value="Intermediate">Intermediate</option>
                <option value="Advanced">Advanced</option>
              </select>
              <button
                onClick={() => handlePracticeClick(job.job_title, index)}
                style={{
                  marginLeft: "10px",
                  padding: "6px 12px",
                  backgroundColor: "#28a745",
                  color: "#fff",
                  border: "none",
                  borderRadius: "5px",
                  cursor: "pointer"
                }}
              >
                {activePracticeIndex === index ? "Hide Practice" : "Practice Now"}
              </button>

              {/* Practice Section */}
              {activePracticeIndex === index && (
                <div style={{ marginTop: "15px", paddingLeft: "15px" }}>
                  {questionLoading ? (
                    <p>Generating questions...</p>
                  ) : (
                    <ul>
                      {questions[index]?.map((q, qIndex) => (
                        <li key={qIndex} style={{ marginBottom: "10px" }}>
                          <strong>Q{qIndex + 1}:</strong> {q}
                          <br />
                          <button
                            onClick={() => toggleAnswer(index, qIndex)}
                            style={{
                              marginTop: "5px",
                              padding: "4px 10px",
                              fontSize: "12px",
                              backgroundColor: "#007bff",
                              color: "#fff",
                              border: "none",
                              borderRadius: "4px",
                              cursor: "pointer"
                            }}
                          >
                            {showAnswers[`${index}-${qIndex}`] ? "Hide Answer" : "Show Answer"}
                          </button>
                          {showAnswers[`${index}-${qIndex}`] && (
                            <p style={{ marginTop: "5px", backgroundColor: "#f8f9fa", padding: "8px", borderRadius: "5px" }}>
                              <strong>Answer:</strong> {answers[index]?.[qIndex] || "Answer not available"}
                            </p>
                          )}
                        </li>
                      ))}
                    </ul>
                  )}
                </div>
              )}
            </li>
          ))}
        </ul>
      ) : (
        <p>No job matches found based on your resume keywords.</p>
      )}
    </div>
  );
}

export default JobSuggestions;
