import React, { useState } from "react";
import axios from "axios";

function JobSuggestions({ resumeKeywords }) {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedJob, setSelectedJob] = useState(null);
  const [difficulty, setDifficulty] = useState("");
  const [questions, setQuestions] = useState([]);

  // Fetch jobs based on resume keywords
  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const options = {
          method: 'GET',
          url: 'https://jsearch.p.rapidapi.com/search',
          params: {
            query: resumeKeywords.join(" "),
            page: '1',
            num_pages: '1'
          },
          headers: {
            'X-RapidAPI-Key': '',
            'X-RapidAPI-Host': 'jsearch.p.rapidapi.com'
          }
        };
        const res = await axios.request(options);
        setJobs(res.data.data || []);
        setLoading(false);
      } catch (err) {
        console.error("Failed to fetch jobs:", err);
        setLoading(false);
      }
    };

    fetchJobs();
  }, [resumeKeywords]);

  // Handle "Practice Now" click
  const handlePracticeClick = (job) => {
    setSelectedJob(job);
    setDifficulty("");
    setQuestions([]);
  };

  // Handle difficulty selection
  const handleDifficultyChange = async (event) => {
    const selectedDifficulty = event.target.value;
    setDifficulty(selectedDifficulty);

    if (selectedJob && selectedDifficulty) {
      try {
        const response = await axios.post("http://localhost:5000/generate-questions", {
          jobRole: selectedJob.job_title,
          difficulty: selectedDifficulty
        });
        setQuestions(response.data.questions);
      } catch (err) {
        console.error("Failed to fetch questions:", err);
      }
    }
  };

  return (
    <div>
      <h2>üî• Job Suggestions</h2>
      {loading ? (
        <p>Loading job listings...</p>
      ) : (
        <ul>
          {jobs.map((job, index) => (
            <li key={index}>
              <strong>{job.job_title}</strong> at <em>{job.employer_name}</em><br />
              üìç {job.job_city}, {job.job_country}<br />
              <a href={job.job_apply_link} target="_blank" rel="noopener noreferrer">Apply Now</a>
              <button onClick={() => handlePracticeClick(job)}>Practice Now</button>
            </li>
          ))}
        </ul>
      )}

      {selectedJob && (
        <div>
          <h3>Practice Questions for {selectedJob.job_title}</h3>
          <label>Select Difficulty:</label>
          <select value={difficulty} onChange={handleDifficultyChange}>
            <option value="">Select...</option>
            <option value="Beginner">Beginner</option>
            <option value="Intermediate">Intermediate</option>
            <option value="Advanced">Advanced</option>
          </select>

          {questions.length > 0 && (
            <div>
              <h4>Generated Questions:</h4>
              <ul>
                {questions.map((question, idx) => (
                  <li key={idx}>{question}</li>
                ))}
              </ul>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default JobSuggestions;
